import React from "react";

function CreateArea() {
  const [note, setNote] = useState({
    title: "",
    content: "",
  });

  function handleChange(event){
    const {name,value} = event.target;
  }

  return (
    <div>
      <form>
        <input
          name="title"
          onChange={handleChange}
          value={note.title}
          placeholder="Title"
        />
        <textarea
          name="content"
          id=""
          cols="30"
          onChange={handleChange}
          rows="10"
          value={note.content}
          placeholder="Take a note ..."
        ></textarea>
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
