import React from "react";

function CreateArea() {
  const [note, setNote] = useState({
    title: "",
    content: "",
  });

  return (
    <div>
      <form>
        <input name="title" placeholder="Title" />
        <textarea
          name="content"
          id=""
          cols="30"
          rows="10"
          placeholder="Take a note ..."
        ></textarea>
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
